tables.table_schema:
CREATE TABLE renewable_energy_subsidies (
  id INT PRIMARY KEY,
  subsidy_name VARCHAR(100),
  availability BOOLEAN,
  cost DECIMAL(10,2)
);

CREATE TABLE Subsidies (
    subsidy_id INT PRIMARY KEY,
    subsidy_name VARCHAR(50) NOT NULL,
    subsidy_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE EnergyProviders (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(50) NOT NULL,
    provider_location VARCHAR(100) NOT NULL
);

CREATE TABLE EnergyPrices (
    price_id INT PRIMARY KEY,
    provider_id INT REFERENCES EnergyProviders(provider_id),
    subsidy_id INT REFERENCES Subsidies(subsidy_id),
    price DECIMAL(8, 2) NOT NULL
);

CREATE TABLE EligibleTechnologies (
  tech_id INT PRIMARY KEY,
  tech_name VARCHAR(255),
  tech_type VARCHAR(255),
  efficiency FLOAT
);

CREATE TABLE Regions (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(255),
  country VARCHAR(255),
  population INT
);

CREATE TABLE regions (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(50)
);

CREATE TABLE facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(50),
    region_id INT,
    CONSTRAINT fk_region FOREIGN KEY (region_id) REFERENCES regions(region_id)
);

CREATE TABLE providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(50),
    facility_id INT,
    CONSTRAINT fk_facility FOREIGN KEY (facility_id) REFERENCES facilities(facility_id)
);
