tables.table_schema:
CREATE TABLE research_data (
  research_id INT PRIMARY KEY,
  researcher_name VARCHAR(50),
  biofuel_type VARCHAR(50),
  greenhouse_gas_impact DECIMAL
);

CREATE TABLE bioenergy_sources (
  source_id INT PRIMARY KEY,
  source_name VARCHAR(50),
  carbon_intensity DECIMAL,
  production_region VARCHAR(50)
);

CREATE TABLE Researchers (
    researcher_id UUID PRIMARY KEY,
    researcher_name VARCHAR(100) NOT NULL,
    institution VARCHAR(100) NOT NULL
);

CREATE TABLE Biofuels (
    biofuel_id UUID PRIMARY KEY,
    biofuel_name VARCHAR(100) NOT NULL,
    carbon_intensity DECIMAL(10,2) NOT NULL
);

CREATE TABLE GreenhouseGasEmissions (
    emission_id UUID PRIMARY KEY,
    biofuel_id UUID NOT NULL,
    greenhouse_gas VARCHAR(100) NOT NULL,
    emission_amount DECIMAL(10,2) NOT NULL
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    research_area VARCHAR(50)
);

CREATE TABLE biofuels (
    biofuel_id INT PRIMARY KEY,
    biofuel_type VARCHAR(50),
    biofuel_source VARCHAR(50)
);

CREATE TABLE greenhouse_gases (
    gas_id INT PRIMARY KEY,
    gas_name VARCHAR(50)
);
