tables.table_schema:
CREATE TABLE temperature_data (
  reservoir_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  temperature FLOAT
);

CREATE TABLE pressure_data (
  reservoir_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  pressure FLOAT
);

CREATE TABLE production_data (
  reservoir_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  production_rate FLOAT
);

CREATE TABLE temperature (
  id INT PRIMARY KEY,
  temperature_value FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE pressure (
  id INT PRIMARY KEY,
  pressure_value FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE production_rate (
  id INT PRIMARY KEY,
  production_rate_value FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE reservoir (
  reservoir_id INT PRIMARY KEY,
  temperature_id INT,
  pressure_id INT,
  production_rate_id INT
);
