CREATE TABLE mitigation_measures (
  measure_id INT PRIMARY KEY,
  assessment_id INT,
  measure_description TEXT,
  CONSTRAINT fk_assessment_id FOREIGN KEY (assessment_id) REFERENCES environmental_impact_assessments(assessment_id)
);

CREATE TABLE stakeholders (
  stakeholder_id INT PRIMARY KEY,
  stakeholder_name VARCHAR(100),
  contact_info VARCHAR(255)
);
