tables.table_schema:
CREATE TABLE EnergyUsagePatterns (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    energy_usage FLOAT
);

CREATE TABLE RenewableEnergyProduction (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    solar_energy_generated FLOAT,
    wind_energy_generated FLOAT
);

CREATE TABLE EnergyConsumption (
    date DATE,
    time TIME,
    location VARCHAR,
    energy_usage DOUBLE
);

CREATE TABLE RenewableEnergyProduction (
    date DATE,
    time TIME,
    location VARCHAR,
    energy_production DOUBLE
);

CREATE TABLE EnergySources (
    source_id SERIAL PRIMARY KEY,
    source_name VARCHAR,
    source_type VARCHAR
);
