tables.table_schema:
CREATE TABLE smart_meters (
    id UUID PRIMARY KEY,
    meter_id INT,
    timestamp TIMESTAMP,
    energy_consumed FLOAT
);

CREATE TABLE energy_reports (
    report_id UUID PRIMARY KEY,
    meter_id INT,
    report_date DATE,
    total_energy_consumed FLOAT
);

CREATE TABLE smart_meter_data (
    meter_id INT PRIMARY KEY,
    date DATE,
    time TIME,
    energy_consumption DECIMAL
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    name VARCHAR(50),
    address VARCHAR(100)
);

CREATE TABLE meter_locations (
    location_id INT PRIMARY KEY,
    meter_id INT,
    latitude DECIMAL,
    longitude DECIMAL
);
