tables.table_schema:
CREATE TABLE solar_panels (
    panel_id INT PRIMARY KEY,
    panel_location VARCHAR(255),
    panel_capacity FLOAT
);

CREATE TABLE wind_turbines (
    turbine_id INT PRIMARY KEY,
    turbine_location VARCHAR(255),
    turbine_capacity FLOAT
);

CREATE TABLE solar_panel_data (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    power_output FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE electricity_generation_summary (
    id INT PRIMARY KEY,
    generation_date DATE,
    total_power_output FLOAT
);
