tables.table_schema:
CREATE TABLE historical_energy_data (
    timestamp TIMESTAMP,
    energy_consumption DECIMAL,
    temperature DECIMAL,
    location VARCHAR(255)
);

CREATE TABLE energy_optimization (
    optimization_id INT,
    timestamp TIMESTAMP,
    energy_saved DECIMAL,
    optimization_details TEXT
);

CREATE TABLE energy_distribution (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    energy_consumed FLOAT,
    location VARCHAR(255)
);

CREATE TABLE energy_sources (
    id UUID PRIMARY KEY,
    source_type VARCHAR(255),
    capacity FLOAT,
    location VARCHAR(255)
);

CREATE TABLE energy_consumers (
    id UUID PRIMARY KEY,
    consumer_type VARCHAR(255),
    energy_consumption FLOAT,
    location VARCHAR(255)
);
