tables.table_schema:
CREATE TABLE LoadSheddingStrategies (
	strategy_id INT PRIMARY KEY,
	strategy_name VARCHAR(50) NOT NULL,
	data_source VARCHAR(100) NOT NULL
);

CREATE TABLE RealTimeData (
	data_id INT PRIMARY KEY,
	timestamp TIMESTAMP NOT NULL,
	demand INT NOT NULL,
	status VARCHAR(20) NOT NULL
);

CREATE TABLE load_shedding_strategies (
    strategy_id SERIAL PRIMARY KEY,
    strategy_name TEXT NOT NULL,
    load_threshold INT NOT NULL,
    strategy_description TEXT
);

CREATE TABLE real_time_data (
    data_id SERIAL PRIMARY KEY,
    sensor_id TEXT NOT NULL,
    data_value FLOAT NOT NULL,
    timestamp TIMESTAMPTZ NOT NULL
);

CREATE TABLE blackout_avoidance (
    avoidance_id SERIAL PRIMARY KEY,
    strategy_id INT REFERENCES load_shedding_strategies(strategy_id),
    blackout_location TEXT NOT NULL,
    avoidance_time TIMESTAMPTZ NOT NULL
);

CREATE TABLE LoadSheddingZone (
    zone_id INT PRIMARY KEY,
    zone_name VARCHAR(255),
    schedule_start_time TIMESTAMP,
    schedule_end_time TIMESTAMP
);

CREATE TABLE LoadSheddingStrategy (
    strategy_id INT PRIMARY KEY,
    strategy_name VARCHAR(255),
    strategy_description TEXT
);
