tables.table_schema:
CREATE TABLE demand_response_programs (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(255),
    start_datetime TIMESTAMP,
    end_datetime TIMESTAMP,
    target_energy_reduction DECIMAL
);

CREATE TABLE participant_data (
    participant_id INT PRIMARY KEY,
    program_id INT REFERENCES demand_response_programs(program_id),
    energy_usage DECIMAL,
    response_status VARCHAR(50)
);

CREATE TABLE demand_response_programs (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(50) NOT NULL,
    start_time TIME NOT NULL,
    end_time TIME NOT NULL
);

CREATE TABLE participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(50) NOT NULL,
    program_id INT NOT NULL,
    CONSTRAINT fk_program_id FOREIGN KEY (program_id) REFERENCES demand_response_programs(program_id)
);

CREATE TABLE peak_hours (
    hour_id INT PRIMARY KEY,
    start_hour TIME NOT NULL,
    end_hour TIME NOT NULL
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR,
    address VARCHAR,
    phone_number VARCHAR
);

CREATE TABLE Devices (
    device_id INT PRIMARY KEY,
    device_type VARCHAR,
    device_name VARCHAR,
    device_location VARCHAR
);

CREATE TABLE EnergyConsumption (
    device_id INT,
    energy_usage FLOAT,
    timestamp TIMESTAMP,
    PRIMARY KEY (device_id, timestamp)
);

CREATE TABLE DemandResponsePrograms (
    program_id INT PRIMARY KEY,
    program_name VARCHAR,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE ProgramParticipants (
    customer_id INT,
    program_id INT,
    join_date DATE,
    PRIMARY KEY (customer_id, program_id)
);
