tables.table_schema:
CREATE TABLE WeatherData (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE EnergyDemand (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    energy_demand FLOAT,
    grid_operation_mode VARCHAR(50)
);

CREATE TABLE weather_data (
    location_id INT PRIMARY KEY,
    temperature FLOAT,
    humidity FLOAT,
    wind_speed FLOAT
);

CREATE TABLE energy_demand (
    location_id INT PRIMARY KEY,
    demand FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE grid_assets (
    asset_id INT PRIMARY KEY,
    asset_name VARCHAR(50),
    asset_type VARCHAR(50),
    asset_location VARCHAR(100)
);

CREATE TABLE grid_adjustments (
    adjustment_id INT PRIMARY KEY,
    adjustment_type VARCHAR(50),
    adjustment_timestamp TIMESTAMP,
    adjustment_details TEXT
);
