tables.table_schema:
CREATE TABLE smart_grid_data (
    data_id SERIAL PRIMARY KEY,
    data_value TEXT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE smart_grid_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL
);

CREATE TABLE smart_meter_readings (
    reading_id INT PRIMARY KEY,
    meter_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    value FLOAT NOT NULL
);

CREATE TABLE smart_grid_users (
    user_id UUID PRIMARY KEY,
    username STRING NOT NULL,
    password STRING NOT NULL,
    email STRING
);

CREATE TABLE smart_grid_devices (
    device_id UUID PRIMARY KEY,
    device_name STRING NOT NULL,
    location STRING,
    status BOOLEAN NOT NULL
);

CREATE TABLE smart_grid_logs (
    log_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    event STRING NOT NULL,
    user_id UUID
);

CREATE TABLE smart_grid_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL
);

CREATE TABLE smart_grid_permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) NOT NULL
);

CREATE TABLE smart_grid_access_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    access_time TIMESTAMP NOT NULL,
    accessed_table VARCHAR(50) NOT NULL
);

CREATE TABLE smart_grid_security_events (
    event_id INT PRIMARY KEY,
    event_type VARCHAR(50) NOT NULL,
    event_date DATE NOT NULL,
    description TEXT
);

CREATE TABLE smart_grid_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    password VARCHAR(255) NOT NULL
);

CREATE TABLE smart_grid_roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL
);

CREATE TABLE smart_grid_permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) NOT NULL
);

CREATE TABLE smart_grid_user_roles (
    user_id INT,
    role_id INT,
    FOREIGN KEY (user_id) REFERENCES smart_grid_users(user_id),
    FOREIGN KEY (role_id) REFERENCES smart_grid_roles(role_id)
);

CREATE TABLE smart_grid_role_permissions (
    role_id INT,
    permission_id INT,
    FOREIGN KEY (role_id) REFERENCES smart_grid_roles(role_id),
    FOREIGN KEY (permission_id) REFERENCES smart_grid_permissions(permission_id)
);
