tables.table_schema:
CREATE TABLE carbon_emissions (
    id INT PRIMARY KEY,
    initiative VARCHAR(255),
    emission_reduction FLOAT
);

CREATE TABLE initiatives_relationship (
    id INT PRIMARY KEY,
    carbon_emission_id INT,
    renewable_energy_project_id INT
);

CREATE TABLE emission_tracking (
    id INT PRIMARY KEY,
    date DATE,
    emission_amount FLOAT,
    emission_source VARCHAR(255)
);

CREATE TABLE carbon_emissions (
  id UUID PRIMARY KEY,
  initiative_name VARCHAR(50) NOT NULL,
  reduction_amount DECIMAL(10, 2) NOT NULL,
  achieved_date DATE
);

CREATE TABLE emissions_sources (
  id UUID PRIMARY KEY,
  source_name VARCHAR(50) NOT NULL,
  emission_rate DECIMAL(10, 2) NOT NULL,
  source_type VARCHAR(50)
);
