tables.table_schema:
CREATE TABLE storage_units (
    storage_id INT PRIMARY KEY,
    production_data FLOAT,
    consumption_data FLOAT
);

CREATE TABLE energy_tracking (
    tracking_id INT PRIMARY KEY,
    storage_id INT,
    time_stamp TIMESTAMP,
    production FLOAT,
    consumption FLOAT,
    FOREIGN KEY (storage_id) REFERENCES storage_units(storage_id)
);

CREATE TABLE storage_units (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE energy_production (
    id INT PRIMARY KEY,
    storage_unit_id INT,
    production_date DATE,
    production_amount DECIMAL
);

CREATE TABLE energy_consumption (
    id INT PRIMARY KEY,
    storage_unit_id INT,
    consumption_date DATE,
    consumption_amount DECIMAL
);

CREATE TABLE storage_units (
  id INT PRIMARY KEY,
  unit_name VARCHAR(255) NOT NULL,
  capacity DECIMAL NOT NULL
);
