tables.table_schema:
CREATE TABLE energy_storage_capacity ( 
    id INT PRIMARY KEY, 
    timestamp TIMESTAMP, 
    energy_consumption FLOAT, 
    energy_generation FLOAT 
);

CREATE TABLE energy_consumption (
    date DATE,
    time TIME,
    location VARCHAR,
    energy_usage FLOAT
);

CREATE TABLE storage_capacity (
    date DATE,
    capacity FLOAT,
    location VARCHAR
);

CREATE TABLE EnergyStorageCapacity (
   id INT PRIMARY KEY,
   date DATE,
   energy_consumed DOUBLE,
   energy_generated DOUBLE
);

CREATE TABLE EnergyStorageHistory (
   id INT PRIMARY KEY,
   date DATE,
   energy_level DOUBLE,
   operation TEXT
);

CREATE TABLE EnergyStorageOptimization (
   id INT PRIMARY KEY,
   date DATE,
   optimization_strategy TEXT,
   efficiency DOUBLE
);

CREATE TABLE EnergyConsumptionData (
  date DATE,
  time TIME,
  energy_consumed FLOAT
);

CREATE TABLE EnergyProductionData (
  date DATE,
  time TIME,
  energy_produced FLOAT
);

CREATE TABLE EnergyStorage (
  storage_id SERIAL PRIMARY KEY,
  energy_level FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE EnergyDemandForecast (
  date DATE,
  time TIME,
  forecasted_demand FLOAT
);

CREATE TABLE EnergyConsumption (
    customer_id INT PRIMARY KEY,
    consumption_date DATE,
    consumption_amount FLOAT
);

CREATE TABLE EnergyProduction (
    production_id INT PRIMARY KEY,
    production_date DATE,
    production_amount FLOAT
);

CREATE TABLE EnergyStorage (
    storage_id INT PRIMARY KEY,
    storage_capacity FLOAT
);

CREATE TABLE EnergyUtilization (
    utilization_id INT PRIMARY KEY,
    utilization_date DATE,
    utilization_amount FLOAT
);

CREATE TABLE EnergyOptimization (
    optimization_id INT PRIMARY KEY,
    optimization_date DATE,
    optimization_strategy TEXT
);
