tables.table_schema:
CREATE TABLE battery_alerts (
    alert_id INT PRIMARY KEY,
    battery_id INT,
    alert_type VARCHAR(50),
    alert_description TEXT
);

CREATE TABLE battery_status (
    battery_id INT PRIMARY KEY,
    battery_name VARCHAR(50),
    battery_level INT,
    last_maintenance_date DATE
);

CREATE TABLE BatteryStatus (
    id SERIAL PRIMARY KEY,
    battery_id INT,
    battery_percentage INT,
    maintenance_required BOOLEAN
);

CREATE TABLE Users (
    user_id SERIAL PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE Alerts (
    alert_id SERIAL PRIMARY KEY,
    battery_id INT,
    alert_type TEXT,
    alert_description TEXT
);

CREATE TABLE battery_information (
  battery_id UUID PRIMARY KEY,
  battery_status VARCHAR,
  battery_voltage FLOAT
);

CREATE TABLE alerts (
  alert_id SERIAL PRIMARY KEY,
  battery_id UUID,
  alert_date TIMESTAMP,
  alert_message TEXT
);

CREATE TABLE battery_history (
  battery_id UUID,
  history_date DATE,
  history_event TEXT
);
