tables.table_schema:
CREATE TABLE EnergyDemandForecast (
	forecast_date DATE,
	forecast_time TIME,
	demand_kwh DECIMAL,
	forecasted_demand_kwh DECIMAL
);

CREATE TABLE ChargingSchedule (
	schedule_date DATE,
	schedule_time TIME,
	charging_station_id INT,
	charging_duration_hours DECIMAL,
	energy_consumed_kwh DECIMAL
);

CREATE TABLE energy_demand (
    timestamp TIMESTAMP PRIMARY KEY,
    location_id UUID,
    demand_kwh DOUBLE PRECISION
);

CREATE TABLE charging_schedule (
    schedule_id UUID PRIMARY KEY,
    location_id UUID,
    charging_start TIMESTAMP,
    charging_end TIMESTAMP
);

CREATE TABLE locations (
    location_id UUID PRIMARY KEY,
    location_name VARCHAR,
    address TEXT
);

CREATE TABLE weather_data (
    timestamp TIMESTAMP PRIMARY KEY,
    location_id UUID,
    temperature DOUBLE PRECISION,
    weather_conditions VARCHAR
);
