tables.table_schema:
CREATE TABLE storage_performance (
  id INT PRIMARY KEY,
  storage_name VARCHAR(50),
  used_capacity DECIMAL,
  total_capacity DECIMAL
);

CREATE TABLE dashboard_data (
  dashboard_id INT PRIMARY KEY,
  storage_id INT,
  dashboard_name VARCHAR(100),
  last_updated TIMESTAMP
);

CREATE TABLE storage_metrics (
    storage_id INT PRIMARY KEY,
    storage_name VARCHAR(50),
    total_capacity INT,
    used_capacity INT
);

CREATE TABLE dashboard_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE dashboard_activity (
    activity_id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE storage_performance_dashboards (
    dashboard_id INT PRIMARY KEY,
    dashboard_name VARCHAR(50) NOT NULL,
    creation_date TIMESTAMP
);
CREATE TABLE storage_metrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(50) NOT NULL,
    metric_value DECIMAL(10,2) NOT NULL,
    timestamp TIMESTAMP
);
CREATE TABLE storage_alerts (
    alert_id INT PRIMARY KEY,
    alert_name VARCHAR(50) NOT NULL,
    alert_description TEXT,
    alert_timestamp TIMESTAMP
);
CREATE TABLE storage_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    role VARCHAR(20)
);

CREATE TABLE storage_performance_data (
    id INT PRIMARY KEY,
    metric_name VARCHAR(50),
    metric_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE dashboard_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255),
    role VARCHAR(20)
);

CREATE TABLE dashboard_views (
    view_id INT PRIMARY KEY,
    view_name VARCHAR(100),
    created_date DATE
);

CREATE TABLE storage_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(100),
    device_type VARCHAR(50),
    capacity DECIMAL(10,2),
    location VARCHAR(100)
);

CREATE TABLE dashboard_permissions (
    permission_id INT PRIMARY KEY,
    user_id INT,
    view_id INT,
    permission_level VARCHAR(20)
);
