tables.table_schema:
CREATE TABLE Demand_Response_Programs (
  program_id INT PRIMARY KEY,
  program_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE Stored_Energy (
  energy_id INT PRIMARY KEY,
  energy_source VARCHAR(255),
  energy_amount DECIMAL(10,2),
  storage_location VARCHAR(255)
);

CREATE TABLE participant (
    participant_id UUID PRIMARY KEY,
    participant_name STRING,
    energy_usage DECIMAL,
    program_id UUID REFERENCES program
);

CREATE TABLE energy_storage (
    storage_id UUID PRIMARY KEY,
    storage_location STRING,
    capacity DECIMAL,
    stored_energy DECIMAL
);

CREATE TABLE DemandResponsePrograms (
	drp_id INT PRIMARY KEY,
	drp_name VARCHAR(50) NOT NULL,
	description TEXT
);

CREATE TABLE EnergyStorageSystems (
	es_id INT PRIMARY KEY,
	es_name VARCHAR(50) NOT NULL,
	capacity DECIMAL(10,2) NOT NULL
);

CREATE TABLE Participants (
	participant_id INT PRIMARY KEY,
	participant_name VARCHAR(50) NOT NULL,
	address VARCHAR(100) NOT NULL,
	phone_number VARCHAR(15)
);

CREATE TABLE Events (
	event_id INT PRIMARY KEY,
	event_name VARCHAR(50) NOT NULL,
	event_date DATE NOT NULL,
	location VARCHAR(100) NOT NULL
);
