    effectiveness VARCHAR(50)
);

CREATE TABLE PestInfestationData (
   pest_id UUID PRIMARY KEY,
   pest_type VARCHAR(50),
   infestation_level INT,
   infestation_date DATE
);

CREATE TABLE PestControlMeasures (
   control_id UUID PRIMARY KEY,
   control_type VARCHAR(50),
   control_date DATE,
   control_method VARCHAR(100)
);

CREATE TABLE pests (
  pest_id INT PRIMARY KEY,
  pest_name VARCHAR(255) NOT NULL,
  pest_type VARCHAR(100),
  pest_description TEXT
);

CREATE TABLE infestation_sites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255) NOT NULL,
  site_location VARCHAR(100) NOT NULL,
  site_description TEXT
);

CREATE TABLE pest_control_measures (
  measure_id INT PRIMARY KEY,
  measure_name VARCHAR(255) NOT NULL,
  measure_description TEXT,
  measure_type VARCHAR(100)
);
