tables.table_schema:
CREATE TABLE yield_data (
  date DATE,
  yield FLOAT,
  crop_type VARCHAR(50),
  location VARCHAR(100)
);

CREATE TABLE yield_data (
  date DATE NOT NULL,
  yield FLOAT,
  quality_score INT,
  location VARCHAR(255)
);

CREATE TABLE productivity_analysis (
  date DATE NOT NULL,
  productivity_change FLOAT,
  trend_analysis TEXT
);

CREATE TABLE trends (
    id INT PRIMARY KEY,
    date DATE,
    trend_type VARCHAR(50),
    value FLOAT
);

CREATE TABLE productivity (
    id INT PRIMARY KEY,
    date DATE,
    productivity_metric VARCHAR(50),
    value FLOAT
);

CREATE TABLE YieldData (
  id INT PRIMARY KEY,
  date DATE,
  crop_type VARCHAR(50),
  yield_amount FLOAT
);

CREATE TABLE Trends (
  trend_id INT PRIMARY KEY,
  trend_name VARCHAR(50),
  description TEXT
);

CREATE TABLE Productivity (
  productivity_id INT PRIMARY KEY,
  productivity_date DATE,
  productivity_score INT
);

CREATE TABLE Analysis (
  analysis_id INT PRIMARY KEY,
  analysis_date DATE,
  trend_id INT,
  productivity_id INT
);
