tables.table_schema:
CREATE TABLE farm_fields (
  field_id UUID PRIMARY KEY,
  crop_type TEXT,
  planting_date DATE,
  harvest_yield FLOAT
);

CREATE TABLE CropInformation (
  crop_id SERIAL PRIMARY KEY,
  crop_name VARCHAR(255) NOT NULL,
  planting_date DATE,
  harvest_yield DECIMAL
);

CREATE TABLE crop_types (
    id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    crop_description TEXT
);

CREATE TABLE planting_dates (
    id INT PRIMARY KEY,
    crop_id INT,
    planting_date DATE,
    planting_season VARCHAR(50)
);

CREATE TABLE harvest_yields (
    id INT PRIMARY KEY,
    crop_id INT,
    yield_amount DECIMAL(10, 2),
    yield_date DATE
);

CREATE TABLE crop_types (
    id SERIAL PRIMARY KEY,
    name TEXT NOT NULL,
    description TEXT
);

CREATE TABLE crop_types (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL
);

CREATE TABLE fields (
    field_id INT PRIMARY KEY,
    field_name VARCHAR(50) NOT NULL,
    soil_type VARCHAR(50) NOT NULL
);

CREATE TABLE planting_dates (
    date_id INT PRIMARY KEY,
    field_id INT REFERENCES fields(field_id),
    crop_id INT REFERENCES crop_types(crop_id),
    planting_date DATE NOT NULL
);

CREATE TABLE harvest_yields (
    yield_id INT PRIMARY KEY,
    field_id INT REFERENCES fields(field_id),
    crop_id INT REFERENCES crop_types(crop_id),
    yield_amount DECIMAL NOT NULL
);

CREATE TABLE farm (
    farm_id INT PRIMARY KEY,
    farm_name VARCHAR(50) NOT NULL
);
