tables.table_schema:
CREATE TABLE soil_analysis (
  soil_id INT PRIMARY KEY,
  field_id INT,
  nutrient_level DECIMAL
);

CREATE TABLE crop_rotation (
  crop_id INT PRIMARY KEY,
  crop_name VARCHAR(255),
  rotation_period INT
);

CREATE TABLE fields (
    field_id SERIAL PRIMARY KEY,
    field_name VARCHAR(255) NOT NULL,
    soil_nitrogen FLOAT,
    soil_ph FLOAT
);

CREATE TABLE crops (
    crop_id SERIAL PRIMARY KEY,
    crop_name VARCHAR(255) NOT NULL,
    optimal_nitrogen FLOAT,
    optimal_ph FLOAT
);

CREATE TABLE crop_rotations (
    rotation_id SERIAL PRIMARY KEY,
    field_id INT REFERENCES fields(field_id),
    crop_id INT REFERENCES crops(crop_id),
    rotation_date DATE
);

CREATE TABLE field_data (
    field_id INT PRIMARY KEY,
    soil_type VARCHAR(50),
    nutrient_level DECIMAL(10,2)
);

CREATE TABLE farm_location (
    farm_id INT PRIMARY KEY,
    farm_name VARCHAR(50),
    location VARCHAR(100)
);
