tables.table_schema:
CREATE TABLE CropRotationSchedule (
  crop_id INT PRIMARY KEY,
  crop_name VARCHAR(100) NOT NULL,
  planting_date DATE NOT NULL,
  harvest_date DATE NOT NULL
);

CREATE TABLE NutrientContent (
  nutrient_id INT PRIMARY KEY,
  nutrient_name VARCHAR(50) NOT NULL,
  optimal_range VARCHAR(20) NOT NULL
);

CREATE TABLE SoilAnalysis (
  analysis_id INT PRIMARY KEY,
  sample_date DATE NOT NULL,
  nutrient_level DECIMAL(10, 2) NOT NULL,
  crop_id INT REFERENCES CropRotationSchedule(crop_id)
);

CREATE TABLE CropRotationSchedule (
    rotation_id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    planting_date DATE,
    harvest_date DATE
);

CREATE TABLE NutrientDepletion (
    depletion_id INT PRIMARY KEY,
    nutrient_type VARCHAR(255),
    depletion_amount DECIMAL(10,2),
    soil_condition VARCHAR(255)
);

CREATE TABLE NutrientReplenishment (
    replenishment_id INT PRIMARY KEY,
    nutrient_type VARCHAR(255),
    replenishment_amount DECIMAL(10,2),
    soil_condition VARCHAR(255)
);

CREATE TABLE FieldConditions (
    field_id INT PRIMARY KEY,
    field_name VARCHAR(255),
    soil_ph DECIMAL(3,2),
    moisture_level DECIMAL(10,2)
);

CREATE TABLE crop_rotation_schedule (
  id INT PRIMARY KEY,
  crop_name VARCHAR(50),
  planting_date DATE,
  harvesting_date DATE
);

CREATE TABLE nutrient_depletion (
  id INT PRIMARY KEY,
  nutrient_name VARCHAR(50),
  depletion_rate DECIMAL(10,2),
  replenishment_interval INT
);

CREATE TABLE field (
  id INT PRIMARY KEY,
  field_name VARCHAR(50),
  size_acres DECIMAL(10,2)
);
