tables.table_schema:
CREATE TABLE Disease_Outbreaks (
    outbreak_id INT PRIMARY KEY,
    crop_species TEXT,
    outbreak_date DATE,
    outbreak_cause TEXT
);

CREATE TABLE Field (
 field_id INT PRIMARY KEY,
 field_name VARCHAR(255) NOT NULL,
 field_size DECIMAL(10,2) NOT NULL,
 field_location VARCHAR(255) NOT NULL
);

CREATE TABLE Crop (
 crop_id INT PRIMARY KEY,
 crop_name VARCHAR(255) NOT NULL,
 crop_type VARCHAR(50) NOT NULL,
 crop_variety VARCHAR(100) NOT NULL
);

CREATE TABLE Field (
    field_id INT PRIMARY KEY,
    field_name VARCHAR(50) NOT NULL,
    field_size FLOAT NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE Crop (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    crop_type VARCHAR(50) NOT NULL,
    growth_period VARCHAR(50) NOT NULL
);

CREATE TABLE Biodiversity (
    bio_id INT PRIMARY KEY,
    crop_id INT REFERENCES Crop (crop_id),
    field_id INT REFERENCES Field (field_id),
    biodiversity_level INT NOT NULL
);

CREATE TABLE Disease (
    disease_id INT PRIMARY KEY,
    disease_name VARCHAR(50) NOT NULL,
    disease_cause VARCHAR(100) NOT NULL
);

CREATE TABLE FieldDisease (
    field_id INT REFERENCES Field (field_id),
    disease_id INT REFERENCES Disease (disease_id),
    infection_level INT NOT NULL
);
