tables.table_schema:
CREATE TABLE CropRotationData (
  year INT,
  crop_type VARCHAR(255),
  yield_data FLOAT
);

CREATE TABLE YearlyYieldData (
  year INT,
  crop_type VARCHAR(255),
  yield float
);

CREATE TABLE CropRotationPractices (
  practice_id INT,
  crop_type VARCHAR(255),
  rotation_years INT
);

CREATE TABLE crop_yield_data (
  year INT,
  crop_type VARCHAR(50),
  yield_amount FLOAT
);

CREATE TABLE crop_rotation (
  year INT,
  crop_type VARCHAR(50),
  rotation_order INT
);

CREATE TABLE crop_rotation_history (
  year INT,
  previous_crop VARCHAR(50),
  current_crop VARCHAR(50),
  rotation_order INT
);

CREATE TABLE farmers (
    farmer_id INT PRIMARY KEY,
    farmer_name VARCHAR(50) NOT NULL,
    farmer_location VARCHAR(50) NOT NULL
);

CREATE TABLE crops (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50) NOT NULL,
    crop_type VARCHAR(50) NOT NULL
);

CREATE TABLE crop_rotation (
    rotation_id INT PRIMARY KEY,
    crop_id INT NOT NULL,
    farmer_id INT NOT NULL,
    rotation_year INT NOT NULL
);

CREATE TABLE years_yield (
    year INT,
    yield FLOAT
);

CREATE TABLE crop_rotation (
    rotation_id SERIAL PRIMARY KEY,
    crop_name VARCHAR(50),
    rotation_cycle INT,
    last_planted DATE
);

CREATE TABLE farm_operations (
    operation_id SERIAL PRIMARY KEY,
    operation_name VARCHAR(50),
    operation_date DATE,
    operation_type VARCHAR(50)
);

CREATE TABLE comparison_data (
    comparison_id SERIAL PRIMARY KEY,
    crop_name VARCHAR(50),
    year INT,
    yield FLOAT
);
