tables.table_schema:
CREATE TABLE crop_rotation_experiments (
    experiment_id INT PRIMARY KEY,
    rotation_sequence VARCHAR(50),
    soil_health_score FLOAT
);

CREATE TABLE rotation_sequence_details (
    sequence_id INT PRIMARY KEY,
    crop_name VARCHAR(50),
    planting_date DATE,
    harvest_date DATE
);

CREATE TABLE experiment_data (
  experiment_id INT PRIMARY KEY,
  crop_name VARCHAR,
  rotation_sequence VARCHAR,
  soil_health_rating INT
);

CREATE TABLE crop_rotation (
  rotation_id INT PRIMARY KEY,
  crop_name VARCHAR,
  crop_family VARCHAR,
  crop_rotation_sequence VARCHAR
);

CREATE TABLE soil_health_ratings (
  rating_id INT PRIMARY KEY,
  rating_description TEXT
);

CREATE TABLE CropRotationExperiment (
    experiment_id INT PRIMARY KEY,
    rotation_sequence VARCHAR(50),
    soil_health_score DECIMAL
);

CREATE TABLE Crop (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(50),
    crop_family VARCHAR(50)
);
