tables.table_schema:
CREATE TABLE Livestock (
    id SERIAL PRIMARY KEY,
    animal_type VARCHAR(50),
    weight DECIMAL,
    birth_date DATE
);

CREATE TABLE WeightGain (
    id SERIAL PRIMARY KEY,
    livestock_id INT,
    weight_gain DECIMAL,
    measurement_date DATE
);

CREATE TABLE LivestockFarm (
    id SERIAL PRIMARY KEY,
    farm_name VARCHAR(100),
    location VARCHAR(100),
    established_date DATE
);

