tables.table_schema:
CREATE TABLE LivestockSales (
    id INT PRIMARY KEY,
    livestock_type VARCHAR(50),
    price DECIMAL(10, 2),
    sale_date DATE
);

CREATE TABLE LivestockPurchases (
    id INT PRIMARY KEY,
    livestock_type VARCHAR(50),
    quantity INT,
    purchase_date DATE
);

CREATE TABLE FinancialRecords (
    id INT PRIMARY KEY,
    transaction_type VARCHAR(50),
    amount DECIMAL(10, 2),
    transaction_date DATE
);

CREATE TABLE Sales (
    sale_id SERIAL PRIMARY KEY,
    livestock_id INT REFERENCES Livestock(id),
    sale_date DATE,
    sale_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE Purchases (
    purchase_id SERIAL PRIMARY KEY,
    livestock_id INT REFERENCES Livestock(id),
    purchase_date DATE,
    purchase_price DECIMAL(10, 2) NOT NULL
);
