tables.table_schema:
CREATE TABLE feed_data (
  id INT PRIMARY KEY,
  feed_name VARCHAR(255),
  protein_content FLOAT,
  carbohydrate_content FLOAT
);

CREATE TABLE livestock_group (
  group_id INT PRIMARY KEY,
  group_name VARCHAR(255),
  average_weight FLOAT,
  feed_id INT REFERENCES feed_data(id)
);

CREATE TABLE LivestockGroup ( 
  group_id INT PRIMARY KEY, 
  group_name VARCHAR(50) NOT NULL 
);

CREATE TABLE group_livestock (
    group_id INT PRIMARY KEY,
    group_name VARCHAR(50),
    average_weight DECIMAL(6, 2),
    average_age DECIMAL(3, 1)
);

CREATE TABLE feed_group_mapping (
    feed_id INT,
    group_id INT,
    FOREIGN KEY (feed_id) REFERENCES feed_data(id),
    FOREIGN KEY (group_id) REFERENCES group_livestock(group_id)
);

CREATE TABLE Group_Nutrition (
  group_id INT REFERENCES Group(id),
  nutrition_id INT REFERENCES Nutrition(id),
  recommended_quantity DECIMAL,
  PRIMARY KEY (group_id, nutrition_id)
);
