tables.table_schema:
CREATE TABLE breeding_records (
    record_id SERIAL PRIMARY KEY,
    animal_id INT,
    breeding_date DATE,
    sire_id INT
);

CREATE TABLE Livestock {id INT PRIMARY KEY,
name VARCHAR(255),
age INT,
breed VARCHAR(255),
estrus_cycle_status BOOLEAN
};

CREATE TABLE EstrusCycles {id INT PRIMARY KEY,
livestock_id INT,
start_date DATE,
end_date DATE
};

CREATE TABLE livestock_info (
id INT PRIMARY KEY,
animal_type VARCHAR(255),
estrus_cycle_date DATE,
pregnancy_status VARCHAR(50)
);

CREATE TABLE breeding_records (
record_id INT PRIMARY KEY,
animal_id INT,
breeding_date DATE,
breeding_type VARCHAR(50)
);

CREATE TABLE health_records (
record_id INT PRIMARY KEY,
animal_id INT,
visit_date DATE,
health_condition VARCHAR(255)
);

CREATE TABLE owner_details (
owner_id INT PRIMARY KEY,
owner_name VARCHAR(255),
owner_address VARCHAR(255)
);

CREATE TABLE breeding_center (
center_id INT PRIMARY KEY,
center_name VARCHAR(255),
center_location VARCHAR(255)
);
