tables.table_schema:
CREATE TABLE LivestockMortality (
   id INT PRIMARY KEY,
   animal_type VARCHAR(255),
   cause_of_death VARCHAR(255),
   date_of_death DATE
);

CREATE TABLE HerdHealthManagement (
   id INT PRIMARY KEY,
   animal_type VARCHAR(255),
   health_status VARCHAR(255),
   management_strategy TEXT
);

CREATE TABLE LivestockMortality (
  id SERIAL PRIMARY KEY,
  livestock_id INT NOT NULL,
  mortality_date DATE NOT NULL,
  mortality_cause VARCHAR(255) NOT NULL
);

CREATE TABLE Herd (
  id SERIAL PRIMARY KEY,
  herd_name VARCHAR(100) NOT NULL,
  total_livestock INT NOT NULL
);

CREATE TABLE ManagementStrategies { 
  StrategyID INT PRIMARY KEY, 
  Name VARCHAR(50), 
  Description TEXT 
}
