tables.table_schema:
CREATE TABLE research_team (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50),
  field_of_study VARCHAR(50),
  contact_email VARCHAR(50)
);

CREATE TABLE Fields (
    field_id INT PRIMARY KEY,
    field_name VARCHAR(50) NOT NULL,
    field_location VARCHAR(100),
    field_size FLOAT
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    researcher_email VARCHAR(100),
    researcher_affiliation VARCHAR(100)
);

CREATE TABLE fields (
    field_id INT PRIMARY KEY,
    field_name TEXT NOT NULL,
    soil_type TEXT,
    soil_ph DOUBLE
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name TEXT NOT NULL,
    research_area TEXT
);
