tables.table_schema:
CREATE TABLE yield_data (
  yield_id UUID PRIMARY KEY,
  plot_id UUID REFERENCES plot_location(plot_id),
  yield_amount DECIMAL(10,2) NOT NULL,
  yield_date DATE NOT NULL
);

CREATE TABLE Plot (
    plot_id INT PRIMARY KEY,
    plot_name VARCHAR,
    location_id INT,
    crop_id INT
);

CREATE TABLE Location (
    location_id INT PRIMARY KEY,
    city VARCHAR,
    state VARCHAR
);

CREATE TABLE Crop (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR,
    crop_type VARCHAR,
    crop_season VARCHAR
);

CREATE TABLE YieldData (
    yield_id INT PRIMARY KEY,
    data_id INT,
    yield_amount FLOAT,
    quality_rating VARCHAR
);
