tables.table_schema:
CREATE TABLE climate_conditions (
    id INT PRIMARY KEY,
    date DATE,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE farming_activities (
    id INT PRIMARY KEY,
    date DATE,
    activity_type VARCHAR(255),
    impact TEXT
);

CREATE TABLE WeatherConditions (
    id INT PRIMARY KEY,
    date DATE,
    temperature FLOAT,
    humidity FLOAT
);
CREATE TABLE FarmingActivities (
    id INT PRIMARY KEY,
    activity_name VARCHAR(255),
    activity_start DATETIME,
    activity_end DATETIME
);
CREATE TABLE WeatherImpact (
    id INT PRIMARY KEY,
    weather_condition_id INT,
    impact_description TEXT,
    FOREIGN KEY (weather_condition_id) REFERENCES WeatherConditions(id)
);

CREATE TABLE weather_conditions (
    id INT PRIMARY KEY,
    temperature INT,
    humidity INT,
    wind_speed INT
);

CREATE TABLE farming_activities (
    id INT PRIMARY KEY,
    activity_name VARCHAR(255),
    activity_description TEXT,
    date DATE
);

CREATE TABLE weather_impact (
    id INT PRIMARY KEY,
    condition_id INT,
    activity_id INT,
    impact_level VARCHAR(50)
);

CREATE TABLE locations (
    id INT PRIMARY KEY,
    location_name VARCHAR(255),
    latitude DECIMAL(10, 8),
    longitude DECIMAL(11, 8)
);
