tables.table_schema:
CREATE TABLE soil_quality_zone (
    zone_id INT PRIMARY KEY,
    zone_name STRING,
    ph_level FLOAT,
    moisture_level FLOAT
);

CREATE TABLE SoilQuality (
   zone_id INT,
   ph_level FLOAT,
   moisture_level FLOAT
);

CREATE TABLE FarmingZones (
   zone_id INT,
   zone_name VARCHAR(50),
   location VARCHAR(100)
);

CREATE TABLE SoilQuality (
  zone_id UUID,
  pH_level DECIMAL,
  moisture_level DECIMAL,
  temperature DECIMAL
);

CREATE TABLE NutrientLevels (
  zone_id UUID,
  nitrogen_level DECIMAL,
  phosphorus_level DECIMAL,
  potassium_level DECIMAL
);

CREATE TABLE FarmingZones (
  zone_id UUID PRIMARY KEY,
  zone_name VARCHAR(100),
  city VARCHAR(100),
  country VARCHAR(100)
);

CREATE TABLE MonitoringLogs (
  log_id UUID PRIMARY KEY,
  zone_id UUID,
  timestamp TIMESTAMP,
  status VARCHAR(50)
);

CREATE TABLE UrbanFarmingZones (
  zone_id INT PRIMARY KEY,
  zone_name VARCHAR(50) NOT NULL,
  soil_quality VARCHAR(50) NOT NULL,
  nutrient_level FLOAT NOT NULL
);

CREATE TABLE SoilSensors (
  sensor_id INT PRIMARY KEY,
  zone_id INT REFERENCES UrbanFarmingZones(zone_id) NOT NULL,
  sensor_type VARCHAR(50) NOT NULL,
  sensor_location VARCHAR(50) NOT NULL
);

CREATE TABLE Farmers (
  farmer_id INT PRIMARY KEY,
  farmer_name VARCHAR(50) NOT NULL,
  farmer_address VARCHAR(100) NOT NULL
);

CREATE TABLE ZoneFarmers (
  zone_id INT REFERENCES UrbanFarmingZones(zone_id) NOT NULL,
  farmer_id INT REFERENCES Farmers(farmer_id) NOT NULL
);
