tables.table_schema:
CREATE TABLE Farm (
    farm_id INT PRIMARY KEY,
    farm_name VARCHAR(255) NOT NULL,
    farm_location VARCHAR(255) NOT NULL
);

CREATE TABLE irrigation_schedule (
   id SERIAL PRIMARY KEY,
   date DATE,
   time TIME,
   duration INTERVAL
);

CREATE TABLE water_usage (
   id SERIAL PRIMARY KEY,
   date DATE,
   amount DECIMAL(10,2),
   sensor_id INT
);

CREATE TABLE farm_fields (
   id SERIAL PRIMARY KEY,
   name VARCHAR(50),
   area DECIMAL(10,2),
   crop_type VARCHAR(50)
);

CREATE TABLE Farm (
  farm_id UUID PRIMARY KEY,
  farm_name VARCHAR(255) NOT NULL,
  location VARCHAR(255) NOT NULL
);
