tables.table_schema:
CREATE TABLE FoodItems (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    calories INT NOT NULL,
    protein INT NOT NULL,
    carbs INT NOT NULL
);

CREATE TABLE NutritionalInfo (
    food_id INT REFERENCES FoodItems(id),
    serving_size VARCHAR(20) NOT NULL,
    fat INT NOT NULL,
    fiber INT NOT NULL,
    sugar INT NOT NULL
);

CREATE TABLE DietaryRequirements (
    user_id INT,
    protein_goal INT NOT NULL,
    carb_goal INT NOT NULL,
    calorie_limit INT NOT NULL
);
