tables.table_schema:
CREATE TABLE food_distribution_centers (
  id INT PRIMARY KEY,
  center_name VARCHAR(255),
  location VARCHAR(255)
);

CREATE TABLE FoodDistributionCenter (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    location VARCHAR(255),
    capacity INT
);

CREATE TABLE FoodItem (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    category VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE FoodSupplier (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL,
    supplier_contact VARCHAR(50) NOT NULL
);

CREATE TABLE FoodInventory (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255) NOT NULL,
    item_quantity INT DEFAULT 0
);

CREATE TABLE DistributionCenter (
    center_id INT PRIMARY KEY,
    center_name VARCHAR(255) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE DistributionAllocation (
    allocation_id INT PRIMARY KEY,
    center_id INT NOT NULL,
    item_id INT NOT NULL,
    allocation_quantity INT NOT NULL
);

CREATE TABLE Food_Items (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(50),
    category VARCHAR(50),
    unit_price DECIMAL(10, 2)
);

CREATE TABLE Food_Distribution_Logs (
    log_id INT PRIMARY KEY,
    record_id INT,
    action VARCHAR(50),
    timestamp TIMESTAMP
);
