tables.table_schema:
CREATE TABLE FoodConsumptionPatterns (
  id INT PRIMARY KEY,
  food_name STRING,
  consumption_amount DECIMAL,
  consumption_date TIMESTAMP
);

CREATE TABLE SustainableFoodProduction (
  product_id INT PRIMARY KEY,
  product_name STRING,
  production_location STRING,
  production_date DATE
);

CREATE TABLE food_consumption_patterns ( 
   user_id INT PRIMARY KEY, 
   date DATE, 
   food_type VARCHAR(50), 
   quantity INT 
);

CREATE TABLE food_trends ( 
   trend_id INT PRIMARY KEY, 
   trend_description TEXT, 
   trend_category VARCHAR(50) 
);

CREATE TABLE sustainable_food_production ( 
   production_id INT PRIMARY KEY, 
   production_date DATE, 
   production_type VARCHAR(50), 
   production_quantity INT 
);

CREATE TABLE food_items ( 
   item_id INT PRIMARY KEY, 
   item_name VARCHAR(100), 
   item_category VARCHAR(50) 
);

CREATE TABLE user_profiles ( 
   profile_id INT PRIMARY KEY, 
   user_name VARCHAR(50), 
   user_age INT, 
   user_location VARCHAR(50) 
);
