tables.table_schema:
CREATE TABLE supplier_information (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    contact_person VARCHAR(255),
    contact_email VARCHAR(255)
);

CREATE TABLE food_safety_standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(255),
    required_certifications VARCHAR(255),
    compliance_level VARCHAR(255)
);

CREATE TABLE quality_checks (
    check_id INT PRIMARY KEY,
    product_id INT REFERENCES products(product_id),
    check_date DATE,
    pass BOOLEAN
);

CREATE TABLE violations (
    violation_id INT PRIMARY KEY,
    inspection_id INT REFERENCES inspections(inspection_id),
    violation_description TEXT,
    fine_amount DECIMAL(10, 2)
);
