tables.table_schema:
CREATE TABLE agroforestry_data (
    id INT PRIMARY KEY,
    tree_type VARCHAR(50),
    planting_date DATE,
    location_id INT
);

CREATE TABLE management_logs (
    log_id INT PRIMARY KEY,
    activity VARCHAR(100),
    log_date TIMESTAMP,
    supervisor_id INT,
    tree_id INT
);

CREATE TABLE agroforestry_policies (
    policy_id UUID PRIMARY KEY,
    policy_name TEXT,
    description TEXT,
    date_effective DATE
);
