tables.table_schema:
CREATE TABLE crop_rotation_schedule (
    crop_id INT PRIMARY KEY,
    crop_name VARCHAR(255),
    planting_date DATE,
    harvest_date DATE
);

CREATE TABLE CropRotationSchedule (
  crop_id INT PRIMARY KEY,
  crop_name VARCHAR(50),
  crop_type VARCHAR(50),
  crop_rotation_order INT
);

CREATE TABLE LandParcel (
  parcel_id INT PRIMARY KEY,
  parcel_area FLOAT,
  parcel_location VARCHAR(100),
  crop_id INT REFERENCES CropRotationSchedule(crop_id)
);

CREATE TABLE AgroforestrySystem (
  system_id INT PRIMARY KEY,
  system_name VARCHAR(50),
  system_type VARCHAR(50),
  parcel_id INT REFERENCES LandParcel(parcel_id)
);
