tables.table_schema:
CREATE TABLE SoilHealth ( 
    plot_id INT PRIMARY KEY, 
    ph_level DECIMAL NOT NULL, 
    nutrient_level DECIMAL NOT NULL 
);

CREATE TABLE AgroforestryPlots ( 
    plot_id INT PRIMARY KEY, 
    plot_size DECIMAL NOT NULL, 
    tree_species VARCHAR(50) NOT NULL 
);

CREATE TABLE SoilHealthMonitoring (
    plot_id INT PRIMARY KEY,
    soil_temperature DECIMAL,
    soil_moisture DECIMAL,
    pH_level DECIMAL
);

CREATE TABLE NutrientLevels (
    plot_id INT PRIMARY KEY,
    nitrogen DECIMAL,
    phosphorus DECIMAL,
    potassium DECIMAL
);

CREATE TABLE AgroforestryPlots (
    plot_id INT PRIMARY KEY,
    location TEXT,
    canopy_coverage DECIMAL,
    tree_species TEXT
);

CREATE TABLE SoilHealthMonitor ( 
    soil_id INT PRIMARY KEY, 
    plot_id INT, 
    pH_level DECIMAL, 
    nutrient_level DECIMAL 
);

CREATE TABLE AgroforestryPlot ( 
    plot_id INT PRIMARY KEY, 
    plot_name VARCHAR(255), 
    location VARCHAR(255), 
    area DECIMAL 
);

CREATE TABLE NutrientLevels ( 
    nutrient_id INT PRIMARY KEY, 
    nutrient_name VARCHAR(255), 
    quantity DECIMAL 
);
