tables.table_schema:
CREATE TABLE forests (
    id INT PRIMARY KEY,
    forest_name TEXT,
    location TEXT,
    tree_species TEXT
);

CREATE TABLE Biodiversity (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(255) NOT NULL,
  abundance INT
);

CREATE TABLE EcosystemServices (
  service_id INT PRIMARY KEY,
  service_name VARCHAR(255) NOT NULL,
  service_description TEXT
);

CREATE TABLE biodata_research_study (
  study_id INT PRIMARY KEY,
  study_name VARCHAR(100),
  study_description TEXT
);

CREATE TABLE biodiversity_data (
  data_id INT PRIMARY KEY,
  study_id INT,
  species_name VARCHAR(100),
  abundance INT
);

CREATE TABLE ecosystem_services (
  service_id INT PRIMARY KEY,
  service_name VARCHAR(100),
  service_description TEXT
);

CREATE TABLE ecosystem_service_data (
  data_id INT PRIMARY KEY,
  service_id INT,
  service_value FLOAT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    full_name VARCHAR(255),
    email VARCHAR(255),
    institution VARCHAR(255)
);

CREATE TABLE publications (
    publication_id INT PRIMARY KEY,
    title VARCHAR(255),
    author VARCHAR(255),
    publication_date DATE
);
