tables.table_schema:
CREATE TABLE Certification_Programs (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(255),
    certification_type VARCHAR(50),
    accreditation_status BOOLEAN
);

CREATE TABLE agroforestry_certification (
    id SERIAL PRIMARY KEY,
    certification_name TEXT NOT NULL,
    certification_type TEXT NOT NULL,
    certification_agency TEXT NOT NULL
);

CREATE TABLE farmers (
    id SERIAL PRIMARY KEY,
    farmer_name TEXT NOT NULL,
    farm_location TEXT NOT NULL,
    certification_id INT REFERENCES agroforestry_certification(id)
);

CREATE TABLE documents (
    id SERIAL PRIMARY KEY,
    document_name TEXT NOT NULL,
    document_type TEXT NOT NULL,
    farmer_id INT REFERENCES farmers(id)
);

CREATE TABLE Agroforestry_Certification_Programs (
  certification_id UUID PRIMARY KEY,
  program_name VARCHAR NOT NULL,
  certification_date TIMESTAMP,
  certification_status VARCHAR
);

CREATE TABLE Farmers (
  farmer_id UUID PRIMARY KEY,
  farmer_name VARCHAR NOT NULL,
  farm_location VARCHAR,
  certification_id UUID REFERENCES Agroforestry_Certification_Programs(certification_id)
);

CREATE TABLE Compliance_Documents (
  document_id UUID PRIMARY KEY,
  document_name VARCHAR NOT NULL,
  document_date TIMESTAMP,
  certification_id UUID REFERENCES Agroforestry_Certification_Programs(certification_id)
);

CREATE TABLE Inspectors (
  inspector_id UUID PRIMARY KEY,
  inspector_name VARCHAR NOT NULL,
  inspector_location VARCHAR
);

CREATE TABLE certification_programs (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(50) NOT NULL,
    certification_type VARCHAR(20) NOT NULL
);

CREATE TABLE farmers (
    farmer_id INT PRIMARY KEY,
    farmer_name VARCHAR(50) NOT NULL,
    farm_size DECIMAL(10,2) NOT NULL,
    location VARCHAR(50) NOT NULL
);

CREATE TABLE documents (
    document_id INT PRIMARY KEY,
    document_type VARCHAR(50) NOT NULL,
    document_date DATE NOT NULL,
    document_description TEXT
);

CREATE TABLE inspections (
    inspection_id INT PRIMARY KEY,
    inspector_id INT NOT NULL,
    inspection_date DATE NOT NULL,
    result BOOLEAN NOT NULL
);
