tables.table_schema:
CREATE TABLE greenhouse_data (
    device_id INT PRIMARY KEY,
    temperature FLOAT NOT NULL,
    humidity FLOAT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE device_info (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50) NOT NULL,
    location VARCHAR(50) NOT NULL,
    last_connection TIMESTAMP
);

CREATE TABLE greenhouse_devices (
    device_id UUID PRIMARY KEY,
    name STRING,
    location STRING,
    status STRING
);

CREATE TABLE temperature_data (
    device_id UUID REFERENCES greenhouse_devices(device_id),
    timestamp TIMESTAMPTZ,
    temperature DECIMAL
);

CREATE TABLE humidity_data (
    device_id UUID REFERENCES greenhouse_devices(device_id),
    timestamp TIMESTAMPTZ,
    humidity DECIMAL
);

CREATE TABLE device ( 
    id INT PRIMARY KEY, 
    name VARCHAR(50), 
    location VARCHAR(100), 
    last_updated TIMESTAMP 
);

CREATE TABLE user ( 
    id INT PRIMARY KEY, 
    username VARCHAR(50), 
    email VARCHAR(100) 
);

CREATE TABLE log ( 
    id INT PRIMARY KEY, 
    action VARCHAR(50), 
    timestamp TIMESTAMP 
);
