tables.table_schema:
CREATE TABLE WaterQualityData ( 
  id INT PRIMARY KEY, 
  date DATE, 
  pH DECIMAL(5,2), 
  temperature DECIMAL(5,2) 
);

CREATE TABLE AquacultureConditions ( 
  id INT PRIMARY KEY, 
  date DATE, 
  ammonia DECIMAL(5,2), 
  oxygen DECIMAL(5,2) 
);

CREATE TABLE WaterQualityData ( 
  id SERIAL PRIMARY KEY, 
  timestamp TIMESTAMP NOT NULL, 
  pH FLOAT, 
  temperature FLOAT 
);

CREATE TABLE AquacultureTanks ( 
  tank_id SERIAL PRIMARY KEY, 
  tank_name VARCHAR(50) NOT NULL, 
  tank_capacity INT 
);

CREATE TABLE water_quality_measurements (
    measurement_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    ph FLOAT,
    temperature FLOAT
);

CREATE TABLE aquaculture_ponds (
    pond_id INT PRIMARY KEY,
    pond_name VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE aquaculture_species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50),
    optimal_ph_range VARCHAR(20),
    optimal_temperature_range VARCHAR(20)
);

CREATE TABLE aquaculture_employees (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    role VARCHAR(50)
);
