CREATE TABLE FishSpecies (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50),
    growth_rate FLOAT,
    feeding_schedule VARCHAR(100)
);

CREATE TABLE GrowthRates (
    growth_rate_id INT PRIMARY KEY,
    species_id INT,
    growth_rate FLOAT,
    FOREIGN KEY (species_id) REFERENCES FishSpecies(species_id)
);

CREATE TABLE BreedingHabits (
    breeding_id INT PRIMARY KEY,
    species_id INT,
    breeding_description TEXT,
    FOREIGN KEY (species_id) REFERENCES FishSpecies(species_id)
);

CREATE TABLE GrowthRates (
    growth_id INT PRIMARY KEY,
    species_id INT NOT NULL,
    growth_rate DECIMAL NOT NULL,
    CONSTRAINT fk_species_id FOREIGN KEY (species_id) REFERENCES FishSpecies(species_id)
);

CREATE TABLE FeedingSchedules (
    schedule_id INT PRIMARY KEY,
    species_id INT NOT NULL,
    feeding_schedule TEXT NOT NULL,
    CONSTRAINT fk_species_id FOREIGN KEY (species_id) REFERENCES FishSpecies(species_id)
);

CREATE TABLE BreedingHabits (
    breeding_id INT PRIMARY KEY,
    species_id INT NOT NULL,
    breeding_habits TEXT NOT NULL,
    CONSTRAINT fk_species_id FOREIGN KEY (species_id) REFERENCES FishSpecies(species_id)
