tables.table_schema:
CREATE TABLE FishSpecies (
    species_id UUID PRIMARY KEY,
    species_name VARCHAR(50),
    average_size DECIMAL,
    habitat VARCHAR(100)
);

CREATE TABLE DiseaseRecords (
    disease_id UUID PRIMARY KEY,
    disease_name VARCHAR(50),
    prevention_methods TEXT,
    treatment_plan TEXT
);

CREATE TABLE PopulationStatistics (
    population_id UUID PRIMARY KEY,
    species_id UUID REFERENCES FishSpecies(species_id),
    total_population INTEGER,
    average_age INTEGER
);

CREATE TABLE Fish_Populations (
    fish_id UUID PRIMARY KEY,
    species VARCHAR(50) NOT NULL,
    health_status VARCHAR(20),
    location VARCHAR(100) NOT NULL
);

CREATE TABLE Diseases (
    disease_id UUID PRIMARY KEY,
    disease_name VARCHAR(50) NOT NULL,
    symptoms VARCHAR(255),
    preventive_measures VARCHAR(255)
);

CREATE TABLE Treatments (
    treatment_id UUID PRIMARY KEY,
    disease_id UUID REFERENCES Diseases(disease_id) ON DELETE CASCADE,
    treatment_name VARCHAR(50) NOT NULL,
    treatment_description VARCHAR(255)
);

CREATE TABLE Fish_Health_Logs (
    log_id UUID PRIMARY KEY,
    fish_id UUID REFERENCES Fish_Populations(fish_id) ON DELETE CASCADE,
    recorded_date DATE NOT NULL,
    health_status VARCHAR(20)
);

CREATE TABLE Fisheries (
    fisheries_id UUID PRIMARY KEY,
    location VARCHAR(100) NOT NULL,
    contact_person VARCHAR(50),
    contact_number VARCHAR(15)
);
